import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getOembed,
  getRecommendations,
  getSimple,
} from "../../services/APIs/MusicAppAPI/MusicAppAPI.js";
import PropTypes from "prop-types";
import { moods } from "./moodConfig.js";

const Container = styled.div`
  max-width: 600px;
  text-align: center;
  margin: auto;
  font-family: Poppins;
  h1 {
    font-weight: 100;
  }
  h2 {
    font-weight: 200;
  }
  background: none;
  color: white;
  padding: 32px 0;
`;

const Title = styled.h1`
  margin-top: 0;
  font-size: 72px;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 48px;
`;

const MoodH2 = styled.h2`
`;

const SimpleScreen = ({ token }) => {
  const [currentPlaylistHTML, setCurrentPlaylistHTML] = useState(null);

  // Set/udpate the specific playlist info
  const setContent = async (mood) => {
    const recs = await getRecommendations(token, mood);
    const oembed = await getOembed(
      recs.data.tracks[0].album.external_urls.spotify
    );
    setCurrentPlaylistHTML(oembed.html);
  };

  return (
    <Container>
      <Title>mood</Title>
      <ButtonContainer>
        {Object.keys(moods).map((key) => {
          const mood = moods[key];
          return (
            <MoodH2 key={mood.tag} id="update-btn" onClick={() => setContent(mood.tag)}>
              {mood.label}
            </MoodH2>
          )
        })}
      </ButtonContainer>
      {currentPlaylistHTML && (
        <div
          className="Container"
          dangerouslySetInnerHTML={{ __html: currentPlaylistHTML }}
        ></div>
      )}
    </Container>
  );
};

SimpleScreen.defaultProps = {
  token: null,
};

SimpleScreen.propTypes = {
  token: PropTypes.string,
};

export default SimpleScreen;
