import { useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";

import { moods } from "./config.js";

const Container = styled.div``;

function SimpleScreen() {
  const [currentMood, setCurrentMood] = useState();
  const [hasError, setHasError] = useState(false);
  const [moodData, setMoodData] = useState();

  useEffect(() => {
    if (currentMood) {
      const resp = getSimple(currentMood);
      console.log(resp);
      return resp ? setCurrentMood(resp) : setHasError(true);
    }
  }, [currentMood]);

  return (
    <Container>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        // what should key be?
        return (
          <div onClick={() => setCurrentMood(moodName)} key={moodName}>
            {mood.displayName}
          </div>
        );
      })}
      <div>{currentMood}</div>
      {hasError && <div>Error</div>}
    </Container>
  );
}

export default SimpleScreen;
