import { useEffect, useState } from "react";
import styled from "styled-components";
import { useSimpleGet } from "../../services/hooks/useSimpleGet.js";
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";

import { moods } from "./config.js";

const Container = styled.div``;

function SimpleScreen() {
  const [currentMood, setCurrentMood] = useState();
  const [hasError, setHasError] = useState(false);
  const [moodData, setMoodData] = useState();

  useEffect(() => {
    if (currentMood) {
      const resp = getSimple(currentMood);
      console.log(resp);
      return resp ? setMoodData(resp) : setHasError(true);
    }
  }, [currentMood]);

  return (
    <Container>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        // what should key be?
        return (
          <div onClick={() => useSimpleGet(moodName)} key={moodName}>
            {mood.displayName}
          </div>
        );
      })}
      {typeof moodData !=="object" && <div>{moodData}</div>}
      {hasError && <div>Error</div>}
    </Container>
  );
}

export default SimpleScreen;
