import { useState } from 'react';
import styled from 'styled-components';
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";

import { moods } from './config.js';

const Container = styled.div``;

function SimpleScreen() {
  const getMusic = (key) => {
    const resp = getSimple(key);
    return resp ? setCurrentMood(resp) : "error";
  }

  const [currentMood, setCurrentMood] = useState();

  return (
    <Container>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        // what should key be?
        return <div onClick={() => getMusic(moodName)} key={moodName}>{mood.displayName}</div>;
      })}
      <div>
        {currentMood}
      </div>
    </Container>
  );
}

export default SimpleScreen;
