import { useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/APIs/MusicAppAPI/MusicAppAPI.js";
import PropTypes from "prop-types";
import { getRandomNumberMinMax } from "../../utils/Utils.js";

const Container = styled.div``;

const getSrc = (externalUrl) => {
  const parts = externalUrl.split("/playlist/");
  const src = parts[0] + "/embed/playlist/" + parts[1];
  return src;
};

const SimpleScreen = ({ token }) => {
  const [hasError, setHasError] = useState(false);
  const [allContent, setAllContent] = useState(null);
  const [currentContent, setCurrentContent] = useState(null);
  const [fetchSuccess, setFetchSuccess] = useState(false);
  const [fetchStarted, setFetchStarted] = useState(false);

  useEffect(() => {
    // Fetch all playlist info once
    if (token && !fetchStarted && !fetchSuccess) {
      try {
        async function fetchData() {
          setFetchStarted(true);
          console.log("test");
          const response = await getSimple(token);
          const data = response.data;
          setAllContent(data);
          setFetchSuccess(true);
        }
        fetchData();
      } catch {
        setHasError(true);
        setFetchSuccess(false);
        setFetchStarted(false);
      }
    }
  }, [token, allContent, fetchStarted, fetchSuccess]);

  // Set/udpate the specific playlist info
  const setContent = () => {
    const obj = allContent[getRandomNumberMinMax(0, 49)];
    setCurrentContent(obj.data);
    console.log(obj.data);
  };

  // Initialize the EmbedController and add event listener
  window.onSpotifyIframeApiReady = (IFrameAPI) => {
    let element = document.getElementById("embed-iframe");
    let options = {
      uri: "spotify:playlist:71I1utEsNeXmb4O28RAeaz",
    };
    let callback = (EmbedController) => {
      document.querySelector("#update-btn").addEventListener((event) => {
        console.log(event);
        if (currentContent?.uri) {
          EmbedController.loadUri(currentContent?.uri);
        }
      });
    };
    IFrameAPI.createController(element, options, callback);
  };

  return (
    <Container>
      <h1>Simple Screen</h1>
      {/* {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        return (
          <div key={moodName} onClick={() => setCurrentMood(moodName)}>
            {mood.displayName}
          </div>
        );
      })} */}
      <h2 id="update-btn" onClick={() => setContent()}>
        Pull random playlist
      </h2>
      {currentContent && (
        <div id="embed-iframe">
          <iframe
            key={currentContent?.uri}
            title="playlist-iframe"
            src={currentContent?.src}
            width="380"
            height="380"
            frameBorder="0"
            allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
          ></iframe>
        </div>
      )}
      {hasError && <div>Error</div>}
    </Container>
  );
};

SimpleScreen.defaultProps = {
  token: null,
};

SimpleScreen.propTypes = {
  token: PropTypes.string,
};

export default SimpleScreen;
