import { getJSON } from "../../APIHelpers";

const URI = "http://127.0.0.1:8000";

export const getClientCredentials = async () => {
  const response = await getJSON(`${URI}/credentials`);
  return response.json();
};

const getRandomNumber = (len = 16) => {
  return Math.floor(
    Math.pow(10, len - 1) + Math.random() * 9 * Math.pow(10, len - 1)
  );
};

export const getAuthorization = async (id, secret) => {
  console.log(getRandomNumber());
  const baseURL = new URL("https://accounts.spotify.com/authorize");
  const params = {
    client_id: id,
    response_type: "code",
    redirect_uri: "http://localhost:3000",
    state: getRandomNumber(),
    // scope: [],
  };
  Object.keys(params).forEach((param) => {
    baseURL.searchParams.append(param, params[param]);
  });
    // const response = await getJSON(baseURL);
//   console.log(response);
    window.location = baseURL;
};
