import { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";
import { store } from "../../utils/store.js";

import { moods } from "./config.js";

const Container = styled.div``;

function SimpleScreen() {
  const [hasError, setHasError] = useState(false);
  const globalState = useContext(store);
  const { dispatch, state } = globalState;
  const { currentMood } = state;

  console.log(state);
  useEffect(() => {
    console.log(currentMood);
  }, [currentMood]);

  return (
    <Container>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        // what should key be?
        return (
          <div
            onClick={() =>
              dispatch({
                type: "UPDATE_SIMPLE_SCREEN",
                payload: { newMood: moodName },
              })
            }
            key={moodName}
          >
            {mood.displayName}
          </div>
        );
      })}
      {/* <div>{state.currentMood}</div> */}
      {hasError && <div>Error</div>}
    </Container>
  );
}

export default SimpleScreen;
