import PropTypes from "prop-types";
import { useEffect } from "react";
import { getClientCredentials } from "../../services/APIs/AuthAPI/AuthAPI";

const getRandomNumber = (len = 16) => {
  return Math.floor(
    Math.pow(10, len - 1) + Math.random() * 9 * Math.pow(10, len - 1)
  );
};

const getAuthorizationCode = async (id) => {
  console.log(getRandomNumber());
  const baseURL = new URL("https://accounts.spotify.com/authorize");
  const params = {
    client_id: id,
    response_type: "code",
    redirect_uri: "http://localhost:3000",
    state: getRandomNumber(),
    // scope: [],
  };
  Object.keys(params).forEach((param) => {
    baseURL.searchParams.append(param, params[param]);
  });
  // is this ok?
  window.location.href = baseURL;
};

const Auth = ({ setAuthData }) => {
  useEffect(() => {
    const urlParams = new URL(window.location);
    const authCode = urlParams.searchParams.get("code");
    if (authCode) {
      setAuthData(authCode);
    }
  }, []);
  const handleLogin = () => {
    try {
      async function fetchData() {
        // is this an anti pattern?
        const creds = await getClientCredentials();
        console.log(creds);
        getAuthorizationCode(creds.client_id);
      }
      fetchData();
    } catch {
      console.error("failed");
    }
    // const data = {};
    // setAuthData(data);
  };
  return <div onClick={() => handleLogin()}>Login</div>;
};

Auth.defaultProps = {
  setAuthData: () => {},
};

Auth.propTypes = {
  setAuthData: PropTypes.func,
};

export default Auth;
