import { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";
import { store } from "../../utils/store.js";

import { moods } from "./config.js";

const Container = styled.div``;

const SimpleScreen = async () => {
  const [hasError, setHasError] = useState(false);
  const [currentMood, setCurrentMood] = useState(null);
  const [currentMoodData, setCurrentMoodData] = useState(null);
  useEffect(() => {
    if (currentMood) {
      console.log(currentMood);
      async function fetchData() {
        const response = await getSimple(currentMood);
        console.log(response);
        return response;
      }
      const data = fetchData();
      // setCurrentMoodData(data);
    } else {
      console.log()
    }
  }, [currentMood]);

  return (
    <Container>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        // what should key be?
        return (
          <div key={moodName}>
            {mood.displayName}
          </div>
        );
      })}
      {/* <div>{currentMoodData || null}</div> */}
      {hasError && <div>Error</div>}
    </Container>
  );
};

export default SimpleScreen;
