import { useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/APIs/MusicAppAPI/MusicAppAPI.js";
import { moods } from "./config.js";
import PropTypes from "prop-types";
import { getRandomNumberMinMax } from "../../utils/Utils.js";

const Container = styled.div``;

const getSrc = (externalUrl) => {
  const parts = externalUrl.split("/playlist/");
  const src = parts[0] + "/embed/playlist/" + parts[1];
  return src;
};

const SimpleScreen = ({ token }) => {
  const [hasError, setHasError] = useState(false);
  const [currentMood, setCurrentMood] = useState(null);
  const [currentContent, setCurrentContent] = useState(null);

  useEffect(() => {
    if (currentMood && token) {
      try {
        async function fetchData() {
          const response = await getSimple(currentMood, token);
          const data = response.data;
          const obj = data[getRandomNumberMinMax(0, 49)];
          console.log(obj);
          setCurrentContent(response.data);
        }
        fetchData();
      } catch {
        setHasError(true);
      }
    }
  }, [currentMood, token,]);

  useEffect(() => {
    window.onSpotifyIframeApiReady = (IFrameAPI) => {
      let element = document.getElementById("embed-iframe");
      let options = {
        uri: currentContent?.uri || "spotify:playlist:71I1utEsNeXmb4O28RAeaz",
      };
      let callback = (EmbedController) => {
        if (currentContent?.uri) {
          EmbedController.loadUri(currentContent?.uri);
          setCurrentContent({ ...currentContent, uri: null });
        }
      };
      IFrameAPI.createController(element, options, callback);
    };
  }, []);

  return (
    <Container>
      <h1>Simple Screen</h1>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        return (
          <div key={moodName} onClick={() => setCurrentMood(moodName)}>
            {mood.displayName}
          </div>
        );
      })}
      {currentContent && (
        <div id="embed-iframe">
          <iframe
            key={currentContent?.uri}
            title="playlist-iframe"
            src={currentContent?.src}
            width="380"
            height="380"
            frameBorder="0"
            allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
          ></iframe>
        </div>
      )}
      {hasError && <div>Error</div>}
    </Container>
  );
};

SimpleScreen.defaultProps = {
  token: null,
};

SimpleScreen.propTypes = {
  token: PropTypes.string,
};

export default SimpleScreen;
