import { useEffect, useState } from "react";
import styled from "styled-components";
import { getSimple } from "../../services/MusicAppAPI/MusicAppAPI.js";
import { moods } from "./config.js";

const Container = styled.div``;

const SimpleScreen = () => {
  const [hasError, setHasError] = useState(false);
  const [currentMood, setCurrentMood] = useState(null);
  const [currentURI, setCurrentURI] = useState(null);
  useEffect(() => {
    if (currentMood) {
      try {
        async function fetchData() {
          const response = await getSimple(currentMood);
          setCurrentURI(response.uri);
        }
        fetchData();
      } catch {
        setHasError(true);
      }
    }
  }, [currentMood]);

  useEffect(() => {
    console.log(currentURI);
    window.onSpotifyIframeApiReady = (IFrameAPI) => {
      let element = document.getElementById("embed-iframe");
      let options = {
        uri: "spotify:playlist:71I1utEsNeXmb4O28RAeaz",
      };
      let callback = (EmbedController) => {};
      IFrameAPI.createController(element, options, callback);
    };
  }, [currentURI]);

  return (
    <Container>
      <h1>Simple Screen</h1>
      {Object.keys(moods).map((moodName) => {
        const mood = moods[moodName];
        return (
          <div key={moodName} onClick={() => setCurrentMood(moodName)}>
            {mood.displayName}
          </div>
        );
      })}
      <div id="embed-iframe">
        <iframe
          title="spotify-embed"
          src="https://open.spotify.com/embed/playlist"
          width="380"
          height="380"
          frameBorder="0"
          allowFullScreen=""
          allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
        ></iframe>
      </div>

      {hasError && <div>Error</div>}
    </Container>
  );
};

export default SimpleScreen;
