import { getJSON } from "../../APIHelpers";

const URI = "http://127.0.0.1:8000";
const SPOTIFY_URI = "https://api.spotify.com";

export const getClientCredentials = async (name) => {
  const response = await getJSON(`${URI}/credentials`);
  return response.json();
};

export const getAuthorization = async () => {
    const baseURL = new URL(`${SPOTIFY_URI}/authorize`);
    await getJSON(`${SPOTIFY_URI}/authorize`)
}
